import{_ as s,c as a,o as t,a as n}from"./app.73871be4.js";const E=JSON.parse('{"title":"Mail Connector","description":"","frontmatter":{},"headers":[{"level":2,"title":"Operations","slug":"operations","link":"#operations","children":[{"level":3,"title":"Validate","slug":"validate","link":"#validate","children":[]},{"level":3,"title":"TestConnection","slug":"testconnection","link":"#testconnection","children":[]},{"level":3,"title":"DeleteMail","slug":"deletemail","link":"#deletemail","children":[]}]},{"level":2,"title":"Configuration","slug":"configuration","link":"#configuration","children":[]}],"relativePath":"connectors/mail.md"}'),e={name:"connectors/mail.md"},l=n(`<h1 id="mail-connector" tabindex="-1">Mail Connector <a class="header-anchor" href="#mail-connector" aria-hidden="true">#</a></h1><p>The Mail connector provides both an IMAP Connector and SMTP server to receive email to be validated from IdMUnit tests.</p><p>The SMTP Server connector is intended to be used for testing in development and testing environments without an email server. It acts as an SMTP server target for processes needing an SMTP server. The SMTP connector keeps a cache of incoming emails and iterates over those emails to find a match. When a match is found it is removed from the incoming queue.</p><p>The IMAP Connector acts as an IMAP client. It iterates through all unread messages in an IMAP inbox looking for a match. When a match is found it is marked as read.</p><p>Both connectors are capable of matching against the full text of a message or against an IDM email template specified by a DN. When matching against an email template, the values of email template fields are specified in individual email field columns.</p><p>The connectors are designed so they may be used interchangeably in different IdMUnit test profiles.</p><h2 id="operations" tabindex="-1">Operations <a class="header-anchor" href="#operations" aria-hidden="true">#</a></h2><h3 id="validate" tabindex="-1">Validate <a class="header-anchor" href="#validate" aria-hidden="true">#</a></h3><p>Validates received email messages. Only one of &quot;Body&quot; or &quot;Template&quot; may be specified.</p><h4 id="params" tabindex="-1">Params <a class="header-anchor" href="#params" aria-hidden="true">#</a></h4><table><thead><tr><th>Name</th><th>Description</th></tr></thead><tbody><tr><td>Body</td><td>The body text to match against.</td></tr><tr><td>Template</td><td>The DN of an IDM email template to match against.</td></tr><tr><td>To</td><td>A TO recipient to match against.</td></tr><tr><td>CC</td><td>A CC recipient to match against.</td></tr><tr><td>From</td><td>A sender to match against.</td></tr><tr><td>[template_properties]</td><td>If &quot;Template&quot; is specified, email template properties to match against.</td></tr></tbody></table><h3 id="testconnection" tabindex="-1">TestConnection <a class="header-anchor" href="#testconnection" aria-hidden="true">#</a></h3><p>Tests the IMAP or SMTP connection. The default folder &quot;Inbox&quot;.</p><h4 id="params-1" tabindex="-1">Params <a class="header-anchor" href="#params-1" aria-hidden="true">#</a></h4><table><thead><tr><th>Name</th><th>Description</th></tr></thead><tbody><tr><td>Mailbox</td><td>The account to authenticate as. (IMAPConnector only)</td></tr><tr><td>Folder</td><td>The folder to attempt to read. (IMAPConnector only)</td></tr></tbody></table><h3 id="deletemail" tabindex="-1">DeleteMail <a class="header-anchor" href="#deletemail" aria-hidden="true">#</a></h3><p>Deletes all mail in the specified folder. The default folder is &quot;Inbox&quot;.</p><h4 id="params-2" tabindex="-1">Params <a class="header-anchor" href="#params-2" aria-hidden="true">#</a></h4><table><thead><tr><th>Name</th><th>Description</th></tr></thead><tbody><tr><td>Mailbox</td><td>The account to authenticate as. (IMAPConnector only)</td></tr><tr><td>Folder</td><td>The folder to delete mail from. If no value is provided, the default is used. (IMAPConnector only)</td></tr></tbody></table><h2 id="configuration" tabindex="-1">Configuration <a class="header-anchor" href="#configuration" aria-hidden="true">#</a></h2><p>This connector does not have any connector specific configuration parameters.</p><div class="language-xml"><button title="Copy Code" class="copy"></button><span class="lang">xml</span><pre class="shiki"><code><span class="line"><span style="color:#81A1C1;">&lt;connection&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;name&gt;</span><span style="color:#D8DEE9FF;">Mail</span><span style="color:#81A1C1;">&lt;/name&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;description&gt;</span><span style="color:#D8DEE9FF;">Mail Connector</span><span style="color:#81A1C1;">&lt;/description&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;type&gt;</span><span style="color:#D8DEE9FF;">com.trivir.idmunit.connector.SMTPServer</span><span style="color:#81A1C1;">&lt;/type&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;port&gt;</span><span style="color:#D8DEE9FF;">25</span><span style="color:#81A1C1;">&lt;/port&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#616E88;">&lt;!-- recipient: An email recipient this connector should track. Case insensitive. May be left empty to denote a catch-all queue --&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;recipient&gt;</span><span style="color:#D8DEE9FF;">test1@example.com</span><span style="color:#81A1C1;">&lt;/recipient&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#616E88;">&lt;!-- An LDAP server must be specified when performing IDM email template validation --&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;ldapserver&gt;</span><span style="color:#D8DEE9FF;">192.168.1.3</span><span style="color:#81A1C1;">&lt;/ldapserver&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;ldapuser&gt;</span><span style="color:#D8DEE9FF;">cn=admin,o=services</span><span style="color:#81A1C1;">&lt;/ldapuser&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;ldappassword&gt;</span><span style="color:#D8DEE9FF;">B2vPD2UsfKc=</span><span style="color:#81A1C1;">&lt;/ldappassword&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;ldaptrust-all-certs&gt;</span><span style="color:#D8DEE9FF;">true</span><span style="color:#81A1C1;">&lt;/ldaptrust-all-certs&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;ldapuse-tls&gt;</span><span style="color:#D8DEE9FF;">true</span><span style="color:#81A1C1;">&lt;/ldapuse-tls&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;multiplier/&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;substitutions/&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;data-injections/&gt;</span></span>
<span class="line"><span style="color:#81A1C1;">&lt;/connection&gt;</span></span>
<span class="line"></span></code></pre></div><div class="language-xml"><button title="Copy Code" class="copy"></button><span class="lang">xml</span><pre class="shiki"><code><span class="line"><span style="color:#81A1C1;">&lt;connection&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;name&gt;</span><span style="color:#D8DEE9FF;">Mail</span><span style="color:#81A1C1;">&lt;/name&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;description&gt;</span><span style="color:#D8DEE9FF;">Mail Connector</span><span style="color:#81A1C1;">&lt;/description&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;type&gt;</span><span style="color:#D8DEE9FF;">com.trivir.idmunit.connector.IMAPConnector</span><span style="color:#81A1C1;">&lt;/type&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;user&gt;</span><span style="color:#D8DEE9FF;">user</span><span style="color:#81A1C1;">&lt;/user&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;password&gt;</span><span style="color:#D8DEE9FF;">B2vPD2UsfKc=</span><span style="color:#81A1C1;">&lt;/password&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;server&gt;</span><span style="color:#D8DEE9FF;">example.com</span><span style="color:#81A1C1;">&lt;/server&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;port&gt;</span><span style="color:#D8DEE9FF;">143</span><span style="color:#81A1C1;">&lt;/port&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;ssl&gt;</span><span style="color:#D8DEE9FF;">false</span><span style="color:#81A1C1;">&lt;/ssl&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;folder&gt;</span><span style="color:#D8DEE9FF;">Inbox</span><span style="color:#81A1C1;">&lt;/folder&gt;</span><span style="color:#D8DEE9FF;"> </span><span style="color:#616E88;">&lt;!-- the default folder (mailbox) is &quot;Inbox&quot; --&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#616E88;">&lt;!-- An LDAP server must be specified when performing IDM email template validation --&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;ldapserver&gt;</span><span style="color:#D8DEE9FF;">192.168.1.3</span><span style="color:#81A1C1;">&lt;/ldapserver&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;ldapuser&gt;</span><span style="color:#D8DEE9FF;">cn=admin,o=services</span><span style="color:#81A1C1;">&lt;/ldapuser&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;ldappassword&gt;</span><span style="color:#D8DEE9FF;">B2vPD2UsfKc=</span><span style="color:#81A1C1;">&lt;/ldappassword&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;ldaptrust-all-certs&gt;</span><span style="color:#D8DEE9FF;">true</span><span style="color:#81A1C1;">&lt;/ldaptrust-all-certs&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;ldapuse-tls&gt;</span><span style="color:#D8DEE9FF;">true</span><span style="color:#81A1C1;">&lt;/ldapuse-tls&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;multiplier/&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;substitutions/&gt;</span></span>
<span class="line"><span style="color:#D8DEE9FF;">    </span><span style="color:#81A1C1;">&lt;data-injections/&gt;</span></span>
<span class="line"><span style="color:#81A1C1;">&lt;/connection&gt;</span></span>
<span class="line"></span></code></pre></div>`,23),o=[l];function p(r,c,i,d,y,h){return t(),a("div",null,o)}const F=s(e,[["render",p]]);export{E as __pageData,F as default};
